C4Deployment
    title Hybrid Cloud Deployment

    Deployment_Node(onprem, "On-Premise DC"){
        Deployment_Node(leg_app, "Legacy App Server"){
            Container(app_l, "Legacy App")
        }
        Deployment_Node(loc_db, "Local DB"){
            ContainerDb(db_l, "On-Prem DB")
        }
    }

    Deployment_Node(cloud, "Cloud Provider", "AWS"){
        Deployment_Node(cld_app, "Cloud App Server"){
            Container(app_c, "Cloud Native App")
        }
        Deployment_Node(cld_db, "Cloud DB Service"){
            ContainerDb(db_c, "Cloud DB")
        }
    }

    Rel(app_l, db_l, "Uses Local DB")
    Rel(app_c, db_c, "Uses Cloud DB")
    Rel(app_c, app_l, "Connects To Legacy", "VPN")

    UpdateRelStyle(app_l, db_l, $offsetY="-20")
    UpdateRelStyle(app_c, db_c, $offsetY="-20")
    UpdateRelStyle(app_c, app_l, $offsetY="30", $offsetX="-30")

