architecture-beta
    group data_ingestion(cloud)[Ingestion]
    group data_storage(cloud)[Storage]

    // Services and nested group within Ingestion
    service source_api(internet)[Source API] in data_ingestion
    service ingest_queue(database)[Ingest Queue] in data_ingestion
    group validators(cloud)[Validators] in data_ingestion

    // Services within Validators
    service validator_1(server)[V1] in validators
    service validator_2(server)[V2] in validators

    // Services within Storage
    service main_db(database)[Main DB] in data_storage
    service data_lake(disk)[Data Lake] in data_storage

    // Connections
    source_api:R --> L:ingest_queue
    ingest_queue:R --> L:validator_1{group}
    validator_1:R --> L:validator_2
    validator_2{group}:R --> L:main_db{group}
    main_db:R --> L:data_lake

architecture-beta
    group G1(cloud)[G1]
    group G2(cloud)[G2]
    group G3(cloud)[G3]

    service S1(server)[S1] in G1
    service S2(server)[S2] in G2
    service S3(server)[S3] in G3

    S1{group}:R --> L:S2{group}
    S2{group}:R --> L:S3{group}
    S3{group}:B --> T:S1{group}

architecture-beta
    // Define Groups
    group FrontendGroup(cloud)[Frontend]
    group APIGroup(cloud)[API]
    group BackendGroup(cloud)[Backend]

    // Define Services outside groups
    service Browser(server)[Client]
    service CDN(internet)[CDN]
    service LoadBalancer(internet)[LB]

    // Define Services within FrontendGroup
    service WebServer1(server)[Web] in FrontendGroup
    service WebServer2(server)[Web] in FrontendGroup

    // Define Services within APIGroup
    service APIServer1(server)[API] in APIGroup
    service APIServer2(server)[API] in APIGroup

    // Define Services within BackendGroup
    service WorkerService(server)[Worker] in BackendGroup
    service MainDatabase(database)[DB] in BackendGroup

    // Define Edges
    Browser:R --> L:CDN
    CDN:R --> L:LoadBalancer
    LoadBalancer:B --> T:WebServer1{group}

    // Edges between groups
    WebServer1{group}:B --> T:APIServer1{group}
    APIServer1{group}:B --> T:WorkerService{group}

    // Edges between services within groups
    WebServer1:R <--> L:WebServer2
    APIServer1:R <--> L:APIServer2

    // Edges between services across/within groups
    APIServer1:R --> L:MainDatabase
    WorkerService:R --> L:MainDatabase

