    class G2C,G2D group2;

graph LR
    P[Pending] --> R(Running)
    R --> D{Decision}
    D -- 'OK' --> S((Success))
    D -- 'Error' --> F>Fail]

    classDef pending fill:yellow,stroke:#333,stroke-width:1px;
    classDef running fill:lightblue,stroke:#333,stroke-width:1px;
    classDef done fill:lightgreen,stroke:#333,stroke-width:1px;

    class P pending;
    class R running;
    class S,D,F done;

graph TB
    Start --> A1;

    subgraph 'Group A'
        A1[Node A1] --> A2(Node A2);
    end

    subgraph 'Group B'
        B1{Node B1} --> B2((Node B2));
    end

    A2 --> B1;
    B2 --> End;

graph LR
    A --> B;
    B -- Dashed -.-> C;
    C == Bold ==> D;
    D -- With Text --> E;

graph LR
    A[NodeA] --> B(NodeB)
    B --> C((NodeC))
    C --> D{NodeD}

    classDef shape1 fill:#f9f,stroke:#333,stroke-width:1px;
    classDef shape2 fill:#fff,stroke:#333,stroke-width:4px;

    class A,C shape1;
    class B,D shape2;

graph TB
    A[This is the Starting Node<br/>with multiple lines] --> B{This is a Decision Node<br/>also with line breaks}
    B --> C((This is the<br/>End Node))

graph TB
    S1[State1]
    S2(State2)
    S3{State3}

    S1 --> S2
    S2 --> S3

    classDef active fill:lightgreen,stroke:#333,stroke-width:1px;
    classDef inactive fill:gray,stroke:#333,stroke-width:1px;

    class S1 active;
    class S2,S3 inactive;

graph TB
    A[Node A]
    B(Node B)
    C{Node C}
    D((Node D))

    A --> B
    B --> C
    C --> D

    classDef base stroke:#333;
    classDef typeA fill:#fdd;
    classDef typeB fill:#ddf;
    classDef important stroke-width:4px,stroke:red;

    class A,B,C,D base;
    class A,C typeA;
    class B,D typeB;
    class C important;

graph LR
    Start --> L_Start;
    subgraph Iteration
        direction TB;
        L_Start(Loop Start) --> L_Cond{Condition?};
        L_Cond -- 'Continue' --> L_Proc[Process];
        L_Proc -.-> L_Cond;
    end
    L_Cond -- 'Exit Loop' --> End;

graph TB
    Start((Start)) --> D1{Is condition A true?};

    D1 -- Yes --> D2{Is condition B true?};
    D1 -- No --> D3{Is condition C true?};

    subgraph Path 1
        D2 -- Yes --> D4{Is condition D true?};
        D2 -- No --> E1((Outcome Alpha));
    end

    subgraph Path 2
        D3 -- Yes --> E2((Outcome Beta));
        D3 -- No --> E3((Outcome Gamma));
    end

    subgraph Path 1.1
        D4 -- Yes --> E4((Outcome Delta));
        D4 -- No --> E5((Outcome Epsilon));
    end

    classDef decision fill:#f9f,stroke:#333,stroke-width:2px;
    classDef endState fill:#ccf,stroke:#333,stroke-width:2px;
    class D1,D2,D3,D4 decision;
    class E1,E2,E3,E4,E5 endState;

