            }

            // Assuming payment was successful to proceed
            order_confirmation = "Order " + res_id + " Confirmed"

            // Send final confirmation back to Client
            @return
            GW->C: order_confirmation

            // Optional: Send confirmation email
            opt {
               GW->C: SendConfirmationEmail
            }

        } catch {
            // --- Handle Order Failure ---
            GW->C: OrderFailed
        } finally {
            // --- Log Transaction Regardless of Outcome ---
